mapper.update:
update article
set article_viewcnt = article_viewcnt + 1
where article_id = #{article_id}

mapper.select:
select ( 
		(select count(*) from tb_course where true
		) + 
		(select count(*) from article where true
		) +
		(select count(*) from club where true
		)
		)

where article_board_type = '공지사항' and true
order by
limit #{pageSize} offset #{offset}

where article_board_type = '이벤트' and true
order by
limit #{pageSize} offset #{offset}


where true

order by

limit #{pageSize} offset #{offset}


where true

order by 
limit #{pageSize} offset #{offset}
